/**
 * Executes a function with exponential backoff retry logic
 * 
 * @param fn - The async function to execute
 * @param options - Configuration options
 * @returns The result of the function
 * @throws The last error encountered after max retries
 */
export async function withBackoff<T>(
  fn: () => Promise<T>,
  options: {
    maxRetries?: number;       // Maximum number of retry attempts
    initialDelay?: number;     // Starting delay in milliseconds
    maxDelay?: number;         // Maximum delay cap in milliseconds
    factor?: number;           // Exponential factor
    jitter?: boolean;          // Add randomness to delay
    onRetry?: (error: Error, attempt: number, delay: number) => void;  // Called before each retry
  } = {}
): Promise<T> {
  const {
    maxRetries = 3,
    initialDelay = 100,
    maxDelay = 10000,
    factor = 2,
    jitter = true,
    onRetry = () => {}
  } = options;

  let attempt = 0;
  let lastError: Error;

  while (attempt <= maxRetries) {
    try {
      return await fn();
    } catch (error) {
      lastError = error instanceof Error ? error : new Error(String(error));

      if (attempt >= maxRetries) {
        break;
      }

      // Calculate delay with exponential backoff
      let delay = initialDelay * Math.pow(factor, attempt);

      // Apply maximum delay cap
      delay = Math.min(delay, maxDelay);

      // Add jitter if enabled (up to 25% variance)
      if (jitter) {
        delay = delay * (0.75 + Math.random() * 0.5);
      }

      // Call the onRetry callback
      onRetry(lastError, attempt + 1, delay);

      // Wait for the calculated delay
      await new Promise(resolve => setTimeout(resolve, delay));

      attempt++;
    }
  }

  throw lastError!;
}

/* Usage examples:

// Simple usage
try {
  const data = await withBackoff(() => fetchData(url));
  console.log(data);
} catch (error) {
  console.error("Failed after multiple retries:", error);
}

// Advanced configuration
const result = await withBackoff(
  () => apiRequest(),
  {
    maxRetries: 5,
    initialDelay: 200,
    maxDelay: 30000,
    factor: 3,
    jitter: true,
    onRetry: (error, attempt, delay) => {
      console.log(`Retry ${attempt} after ${delay}ms due to ${error.message}`);
    }
  }
);
